package com.lzf.tempobj;

public class Img1Text2 {
	private int imgId;
	private String hint;
	private String year;

	public Img1Text2(int imgId, String hint, String year) {
		super();
		this.imgId = imgId;
		this.hint = hint;
		this.year = year;
	}

	public Img1Text2() {
		super();
		// TODO Auto-generated constructor stub
	}

	public int getImgId() {
		return imgId;
	}

	public void setImgId(int imgId) {
		this.imgId = imgId;
	}

	public String getHint() {
		return hint;
	}

	public void setHint(String hint) {
		this.hint = hint;
	}

	public String getYear() {
		return year;
	}

	public void setYear(String year) {
		this.year = year;
	}

}
